$(document).ready(function(){$.ajax({url:"/our-projects/data/projects.json",dataType:"json",success:function(a){var s=[],o=1;$.each(a.projects,function(a,n){""!==n.website?s.push("<div class='project-info'><h3 id='"+o+"'>"+n.title+"<a href='#top' class='visible-xs'><span class='glyphicon glyphicon-chevron-up top'></span></a></h3><h4>"+n.location+"</h4>"+n.aim+n.video+"<br /><p><a href='"+n.website+"' class='btn btn-primary' target='_blank'>Find out more</a><a href='/apply' class='btn btn-info'>Apply now</a></p></div><hr />"):s.push("<div class='project-info'><h3 id='"+o+"'>"+n.title+"<a href='#top' class='visible-xs'><span class='glyphicon glyphicon-chevron-up top'></span></a></h3><h4>"+n.location+"</h4>"+n.aim+n.video+"<br /><p><a href='/apply' class='btn btn-info'>Apply now</a></p></div><hr />"),o++}),$(".show-data").html(s)}})});